import React, { Component } from 'react'
import PropTypes from 'prop-types';

//rce tab

/* Se crei un metodo custom, quindi diverso dai soliti devi fare il bind
 * perchè sia visibile all'interno della tua classe.. anche se è li dentro
 * Con l'arrow function non è necessario invece..
*/ 

export class TodoItem extends Component {

    getStyle = () =>{
        return{
            background: '#f4f4f4',
            padding: '10px',
            borderBottom: '1px #ccc dotted',
            textDecoration: this.props.todo.completed ? 'line-through' : 'none'
        }
    }

    markComplete = (e) => { 
        console.log(this.props)
    }
    
    render() {
        return (
            <div style={this.getStyle()}>
                <p>
                    <input type='checkbox' onChange={this.markComplete}/>
                     {this.props.todo.title } </p>
            </div>
        )
    }
}

//validation to make sure the prop has the right type
TodoItem.propTypes = {
    todo: PropTypes.object.isRequired
}



export default TodoItem
